mapper.insert:
insert into user
values (null, #{name}, #{email}, #{password}, #{gender}, now(), 'USER')




select last_insert_id() from dual;

mapper.select:

select id, name, role
from user
where email = #{email}
  and password = #{password}


select name, email, password, gender
from user
where id = #{id}

mapper.update:
update user
set name     = #{name},
    gender   = #{gender}

        , password = #{password}

where id = #{id};
